import { useEffect } from 'react';
import { gsap } from 'gsap';
import { ScrollToPlugin } from 'gsap/ScrollToPlugin';

gsap.registerPlugin(ScrollToPlugin);

export default function useSmartScrollSnap({
  selector = 'section',
  duration = 0.8,
  threshold = 0.2,
  deadZone = 0.3
}) {
  useEffect(() => {
    let timeout;
    const sections = Array.from(document.querySelectorAll(selector));

    const scrollToY = (y) => {
      gsap.to(window, {
        scrollTo: y,
        duration,
        ease: 'power2.out'
      });
    };

    const onScroll = () => {
      if (timeout) clearTimeout(timeout);

      timeout = setTimeout(() => {
        const scrollY = window.scrollY;
        const viewportHeight = window.innerHeight;

        let snapTarget = null;

        for (let i = 0; i < sections.length; i++) {
          const el = sections[i];
          const rect = el.getBoundingClientRect();
          const elTop = rect.top + scrollY;
          const elHeight = el.offsetHeight;
          const elBottom = elTop + elHeight;

          // --- Special handling: Hero → About (only near boundary) ---
          if (
            i === 0 &&
            scrollY > elBottom - viewportHeight * threshold &&
            scrollY < sections[i + 1]?.offsetTop + viewportHeight * 0.3
          ) {
            snapTarget = sections[i + 1]?.offsetTop;
            break;
          }

          if (elHeight <= viewportHeight) {
            // normal sections
            if (
              scrollY + viewportHeight * threshold >= elTop &&
              scrollY + viewportHeight * threshold < elBottom
            ) {
              snapTarget = elTop;
              break;
            }
          } else {
            // tall section handling
            const deadZoneTop = elTop + elHeight * deadZone;
            const deadZoneBottom = elBottom - viewportHeight * deadZone;

            if (scrollY < deadZoneTop) {
              snapTarget = elTop;
              break;
            } else if (scrollY > deadZoneBottom) {
              if (sections[i + 1]) {
                snapTarget = sections[i + 1].offsetTop;
              }
              break;
            }
          }
        }

        if (snapTarget !== null) {
          scrollToY(snapTarget);
        }
      }, 100);
    };

    window.addEventListener('scroll', onScroll);
    return () => window.removeEventListener('scroll', onScroll);
  }, [selector, duration, threshold, deadZone]);
}
