import React from "react";
import TargetCursor from './TargetCursor';

export default function Hero() {
  return (
    <section className="flex flex-col items-center justify-center text-center py-24">
      <h1 className="text-5xl font-extrabold mb-4 text-purpleAccent">
        Hi, I'm Your Name
      </h1>
      <p className="text-lg max-w-xl">
        A passionate developer building full-stack applications and AI
        integrations.
      </p>
      <a
        href="#projects"
        className="mt-6 inline-block px-6 py-3 bg-purpleAccent text-white rounded-lg hover:opacity-80 transition"
      >
        View My Work
      </a>
    </section>
  );
}
