import React from "react";
import { gsap } from "gsap";
import { ScrollToPlugin } from "gsap/ScrollToPlugin";
import { ScrollTrigger } from "gsap/ScrollTrigger";

import Navbar from "./components/Navbar";
import Hero from "./components/Hero";
import About from "./components/About";
import Projects from "./components/Projects";
import ChatbotSection from "./components/ChatbotSection";
import Footer from "./components/Footer";
import TargetCursor from './components/TargetCursor';
import DarkVeil from './components/DarkVeil';
import ScrollReveal from './components/ScrollReveal';
import useScrollSnap from './components/useScrollSnap';

gsap.registerPlugin(ScrollToPlugin, ScrollTrigger);

export default function App() {
  // Hook runs once, controlling scroll snapping
  useScrollSnap({
    selector: 'section',
    duration: 0.8,
    deadZone: 0.25,
    delayBetweenSnaps: 600
  });

  return (
    <div style={{ width: '100%', minHeight: '100vh', position: 'relative' }}>
      <DarkVeil />
      <div className="darkveil-overlay"></div>

      <div className="site-content">
        <section>
          <Hero />
        </section>
        <section>
          <ScrollReveal baseOpacity={0} enableBlur baseRotation={5} blurStrength={10}>
            <About />
          </ScrollReveal>
        </section>
        <section>
          <ScrollReveal baseOpacity={0} enableBlur baseRotation={5} blurStrength={10}>
            <Projects />
          </ScrollReveal>
        </section>
        <section>
          <ScrollReveal baseOpacity={0} enableBlur baseRotation={5} blurStrength={10}>
            <ChatbotSection />
          </ScrollReveal>
        </section>
        <Footer />
        <TargetCursor spinDuration={2} hideDefaultCursor={true} />
      </div>
    </div>
  );
}
